import "./receiver.css"
import React, { useState, useEffect } from "react"
import axios from "axios";


function Receiver(){
    const [otinput, setOTinput] = useState(-1)

    async function getPublicKeyFromServer(){
        try{
            var value = await axios.get('http://localhost:3001/api/main/getPK');
            value = value.data;
            console.log("got PK from server - " + value);
        } catch(error){
            console.log(error);
        }
    }

    async function handleSubmit(){
        console.log(otinput);
        axios.post('http://localhost:3001/api/main/',{
        "value": otinput,
    });
        try {
            var value = await axios.get('http://localhost:3001/api/main/getMessage');
            value = value.data;
            console.log("got from server - " + value);
        }
        catch(error) {
            console.log(error);
        }
    }

    function Button(props){

        function handleClick(){
            setOTinput(props.value);
        }
        return(
            <>
            <button onClick={handleClick}> {props.value} </button>
            </>
        );
    }
    return (
      <> 
      <h2> Select input <i> i </i></h2>
      <Button value="0"> 0 </Button>
      <Button value="1"> 1 </Button>


      <button type="submit" onClick={handleSubmit}  > Submit </button>


      </>
    );
  }

export default Receiver;
