import React from "react";
import { useForm } from "react-hook-form";

function CreatePrescription(){
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const onSubmit = data => console.log(data);

    return (

    <form onSubmit={handleSubmit(onSubmit)}>
      {/* register your input into the hook by invoking the "register" function */}
      <input defaultValue="Patient ID" {...register("example", {required: true})} />

      {/* include validation with required or other standard HTML validation rules */}
      <input defaultValue="Medicine" {...register("exampleRequired", { required: true })} />
      <input defaultValue="Dosage and Frequency" {...register("exampleRequired", { required: true })} />
      {/* errors will return when field validation fails  */}
      {errors.exampleRequired && <span>This field is required</span>}

      <input type="submit" />
    </form>
  );
}

export default CreatePrescription;
