import React from "react";
import { useForm } from "react-hook-form";
import "./createpres.css"

function Form(){
  return(
    <>
    <link rel="preconnect" href="https://fonts.gstatic.com" />
    <link href="https://fonts.googleapis.com/css2?family=Montserrat:wght@300;400;500&display=swap" rel="stylesheet" /> 

    <div className="container">
    <form >
    <p>Welcome</p>
    <input type="email" placeholder="Email " /><br />
    <input type="password" placeholder="Password" /><br />
    <input type="button" value="Sign in" /><br />

    </form>

      <div class="drops">
        <div class="drop drop-1"></div>
        <div class="drop drop-2"></div>
        <div class="drop drop-3"></div>
        <div class="drop drop-4"></div>
        <div class="drop drop-5"></div>
      </div>
    </div>
    </>
  );
}

function CreatePrescription(){
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const onSubmit = data => console.log(data);

    return (
    <>
    <Form />

    <form onSubmit={handleSubmit(onSubmit)}>


      <input defaultValue="Patient ID" {...register("PatientID", {required: true})} />
      {errors.PatientID && <span>This field is required</span>}

      <input defaultValue="Medicine" {...register("Medicine", { required: true })} />
      {errors.Medicine && <span>This field is required</span>}

      <input defaultValue="Dosage and Frequency" {...register("Quantity", { required: true })} />
      {errors.Quantity && <span>This field is required</span>}

      <input type="submit" />
    </form>
    </>
  );
}

export default CreatePrescription;
